using MySqlX.XDevAPI;using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using C__GestaoVendas.pt.com.Gvendas.CONEXAO;using C__GestaoVendas.pt.com.Gvendas.MODEL;using static System.Runtime.InteropServices.JavaScript.JSType;using MySql.Data.MySqlClient;using System.Data;using C__GestaoVendas.pt.com.Gvendas.VIEWS;namespace C__GestaoVendas.pt.com.Gvendas.DAO{    public class ClienteDAO    {        public MySql.Data.MySqlClient.MySqlConnection connection;        public ClienteDAO ()        {            this.connection = new Connect().GetConnection();        }        #region Registo        public void Registo(Cliente novoClient)        {            try            {                string sql = @"Insert into tb_clientes(nif, nome, profissao, empresa, email, telem, endereco, pos, freguesia, cidade, pais) values (@nif, @nome, @profissao, @empresa, @email, @telem, @endereco, @pos, @freguesia, @cidade, @pais )";                MySqlCommand sqlCommand = new MySqlCommand(sql, connection);                sqlCommand.Parameters.AddWithValue("@nif", novoClient.Nif);                sqlCommand.Parameters.AddWithValue("@nome", novoClient.Nome);                sqlCommand.Parameters.AddWithValue("@profissao", novoClient.Profissao);                sqlCommand.Parameters.AddWithValue("@empresa", novoClient.Empresa);                sqlCommand.Parameters.AddWithValue("@email", novoClient.Email);                sqlCommand.Parameters.AddWithValue("@telem", novoClient.Telem);                sqlCommand.Parameters.AddWithValue("@endereco", novoClient.Endereco);                sqlCommand.Parameters.AddWithValue("@pos", novoClient.Pos);                sqlCommand.Parameters.AddWithValue("@freguesia", novoClient.Freguesia);                sqlCommand.Parameters.AddWithValue("@cidade", novoClient.Cidade);                sqlCommand.Parameters.AddWithValue("@pais", novoClient.Pais);                connection.Open();                sqlCommand.ExecuteNonQuery();                MessageBox.Show("Registo com sucesso!");            }            catch (Exception)            {                MessageBox.Show("Falha no registo.\nVerifique os campos preenchidos.");                throw;            }        }                #endregion    }}